import {
    FETCH_QUADCOPTERS_START,
    FETCH_QUADCOPTERS_SUCCESS,
    FETCH_QUADCOPTERS_FAILURE,
    LOAD_MORE_QUADCOPTERS_START,
    LOAD_MORE_QUADCOPTERS_SUCCESS,
    LOAD_MORE_QUADCOPTERS_FAILURE,
    FETCH_QUADCOPTER_BY_ID_START,
    FETCH_QUADCOPTER_BY_ID_SUCCESS,
    FETCH_QUADCOPTER_BY_ID_FAILURE,
    ADD_QUADCOPTER_TO_BASKET,
    SEARCH_QUADCOPTER,
    FETCH_CATEGORIES_START,
    FETCH_CATEGORIES_SUCCESS,
    FETCH_CATEGORIES_FAILURE,
    REMOVE_QUADCOPTER_FROM_BASKET,
    CLEAN_BASKET
} from 'actionTypes';

import { getRenderedQuadcoptersLength } from 'selectors';

import { 
    fetchQuadcopters as fetchQuadcoptersApi,
    loadMoreQuadcopters as loadMoreQuadcoptersApi,
    fetchQuadcopterById as fetchQuadcopterByIdApi,
    fetchCategories as fetchCategoriesApi
 } from 'api';

export const fetchQuadcopters = () => async dispatch => {
    dispatch({type: FETCH_QUADCOPTERS_START})

    try {
        const quadcopters = await fetchQuadcoptersApi();
        dispatch({
            type: FETCH_QUADCOPTERS_SUCCESS,
            payload: quadcopters
        })
    } catch (err) {
        dispatch({
            type: FETCH_QUADCOPTERS_FAILURE,
            payload: err,
            error: true
        })
    }
};

export const loadMoreQuadcopters = () => async (dispatch, getState) => {
    const offset = getRenderedQuadcoptersLength(getState());

    dispatch({type: LOAD_MORE_QUADCOPTERS_START})

    try {
        const quadcopters = await loadMoreQuadcoptersApi({ offset });
        dispatch({
            type: LOAD_MORE_QUADCOPTERS_SUCCESS,
            payload: quadcopters
        })
    } catch (err) {
        dispatch({
            type: LOAD_MORE_QUADCOPTERS_FAILURE,
            payload: err,
            error: true
        })
    }
};

export const fetchQuadcopterById = id => async dispatch => {
    dispatch({type: FETCH_QUADCOPTER_BY_ID_START})

    try {
         const quadcopter = await fetchQuadcopterByIdApi(id);
         dispatch({
             type: FETCH_QUADCOPTER_BY_ID_SUCCESS,
             payload: quadcopter
         })
    } catch (err) {
        dispatch({
            type: FETCH_QUADCOPTER_BY_ID_FAILURE,
            payload: err,
            error: true
        })
    }
};

export const addQuadcopterToBasket = id => dispatch => {
    dispatch({
        type: ADD_QUADCOPTER_TO_BASKET,
        payload: id
    })
};

export const searchQuadcopter = text => dispatch => {
    dispatch({
        type: SEARCH_QUADCOPTER,
        payload: text
    })
};

export const fetchCategories = () => async dispatch => {
    dispatch({type: FETCH_CATEGORIES_START})

    try {
        const categories = await fetchCategoriesApi();
        dispatch({
            type: FETCH_CATEGORIES_SUCCESS,
            payload: categories
        })
    } catch (err) {
        dispatch({
            type: FETCH_CATEGORIES_FAILURE,
            payload: err,
            error: true
        })
    }
};

export const removeQuadcopterFromBasket = id => dispatch => {
    dispatch({
        type: REMOVE_QUADCOPTER_FROM_BASKET,
        payload: id
    })
};

export const cleanBasket = () => dispatch => {
    dispatch({
        type: CLEAN_BASKET
    })
};

export const basketCheckout = quadcopters => () => {
    alert(JSON.stringify(quadcopters))
};