import quadcopters from './mockQuadcopters';
import categories from './mockCategories';
import { getObjInArrById } from 'helpers/helpers';

export const fetchQuadcopters = async () => {
    return new Promise( (resolve, reject) => {
        resolve(quadcopters)
        // reject("Error")
    })
}

export const loadMoreQuadcopters = async ({ offset }) => {
    return new Promise( (resolve, reject) => {
        resolve(quadcopters)
        // reject("Error")
    })
}

export const fetchQuadcopterById = async id => {
    return new Promise( (resolve, reject) => {
        const quadcopter = getObjInArrById(id, quadcopters);
        resolve(quadcopter) 
    })
}

export const fetchCategories = async() => {
    return new Promise(resolve => {
        resolve(categories)
    })
}