import React, { Component } from 'react';
import { connect } from 'react-redux';

import { searchQuadcopter } from 'actions';

class Search extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value: ""
        }
    }

    handleChange = (event) => {
        this.setState({
            value: event.target.value
        })
    }

    handleSubmit = (event) => {
        event.preventDefault();
        this.props.searchQuadcopter(this.state.value)
    }

    render() {
        return(
            <div className="search-block">
                <h3 className="lead">Quick shop</h3>
                <form onSubmit={this.handleSubmit} className="input-group mb-3">
                    <input
                        onChange={this.handleChange} 
                        type="text"
                        className="form-control"
                        aria-describedby="button-addon"
                    />
                    <span className="input-group-append">
                        <button id="button-addon" className="btn btn-primary ">
                            <span>
                                Search
                            </span>
                        </button>
                    </span>
                </form>
            </div>
        )
    }
}

const saveActionsToProps = {
    searchQuadcopter
}

export default connect(null, saveActionsToProps)(Search);