import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

import { 
    getBasketQuadcoptersWithCount,
    getTotalBasketPrice 
} from 'selectors';

import {
    removeQuadcopterFromBasket,
    cleanBasket,
    basketCheckout
} from 'actions';

const Basket = ({ 
    quadcopters, 
    totalPrice, 
    removeQuadcopterFromBasket,
    cleanBasket,
    basketCheckout
 }) => {
     
    const isBasketEmpty = quadcopters.length < 1;

    const renderContent = () => (
        <div>
            {isBasketEmpty && <div>Your shopping cart is empty.</div>}
            <div className="table-responsive">
                <table 
                    className="
                        table
                        table-bordered 
                        table-striped 
                        "
                >
                    <tbody>
                        {quadcopters.map((quadcopter, index) => (
                            <tr
                                key={index}
                                className="item-checkout"
                            >   
                                <td className="first-column-checkout">
                                    <img 
                                        className="img-thumbnail" 
                                        src={quadcopter.image} 
                                        alt={quadcopter.name}
                                    />
                                </td> 
                                <td>{quadcopter.name}</td>
                                <td>${quadcopter.price}</td>
                                <td>{quadcopter.count}</td>
                                <td>
                                    <span 
                                        onClick={() => removeQuadcopterFromBasket(quadcopter.id)} 
                                        className="delete-cart"
                                    >
                                        X
                                    </span>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            {
                !isBasketEmpty && 
                    <div className="row">
                        <div className="float-right total-user-checkout">
                            <b>Total: </b>
                            ${totalPrice}
                        </div>
                    </div>
            }
        </div>
    )

    const renderSidebar = () => (
        <div className="basket-sidebar">
            <Link
                to="/"
                className="btn btn-info btn-block "
            >
                <span>Continue shopping</span>
            </Link>
            {
                !isBasketEmpty &&
                    <div>
                        <button 
                            onClick={cleanBasket}
                            className="btn btn-danger btn-block"
                        >
                            <i className="fa fa-trash" />
                            <span> Clear cart</span>
                        </button>
                        <button
                            className="btn btn-success btn-block"
                            onClick={() => basketCheckout(quadcopters)}
                        >
                            <i className="fa fa-credit-card-alt" />
                            <span> Checkout</span>
                        </button>
                    </div>
            }
        </div>
    )

    return (
        <div className="view-container">
            <div className="container">
                <div className="row">
                    <div className="col-md-9">
                        {renderContent()}
                    </div>
                    <div className="col-md-3 btn-user-checkout">
                        {renderSidebar()}
                    </div>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = state => ({
    quadcopters: getBasketQuadcoptersWithCount(state),
    totalPrice: getTotalBasketPrice(state)
})

const saveActionsToProps = {
    removeQuadcopterFromBasket,
    cleanBasket,
    basketCheckout
}

export default connect(mapStateToProps, saveActionsToProps)(Basket);