export function itemsByProp(prop, array) {
    let obj = {};
    for (var i=0; i < array.length; i++) {
        for (var key in array[i]) {
            let id = array[i][key];
            if (key === prop) {
                obj[id] = array[i];
            }
        }
    }
    return obj
};

export function addPropToObj(prop, value) {
        let obj = {};
        obj[prop] = value;
    return obj
};

export function getObjById(id, obj) {

    for (var key in obj) {
        if (key === id) {
            return obj[key]
        }
    }
    return {}

};

export function getObjInArrById(id, arr) {

    for (var i=0; i < arr.length; i++) {
        for (var key in arr[i]) {
            if (arr[i][key] === id) {
                return arr[i]
            }
        }
    }
    return {}
};

export function shortenString(str, maxLength) {
    let shortStr = str.slice(0, maxLength);
    if (str.length > maxLength) {
        return shortStr + '...';
    }   
    return str
};

export function isUndefined(item) {
    return (item === undefined)
};

export function objValuesToArr(obj) {
    let arr = [];
    for (let key in obj) {
        arr.push(obj[key])
    }
    return arr
};

export function isFieldValueInObj(value, obj) {

    for (var key in obj) {
        if (obj[key] === value) {
            return true
        }
    }
    return false
};