import {
    ADD_QUADCOPTER_TO_BASKET,
    REMOVE_QUADCOPTER_FROM_BASKET,
    CLEAN_BASKET
} from 'actionTypes';

const initialState = [];

export default (state = initialState, action) => {
    switch(action.type) {
        case ADD_QUADCOPTER_TO_BASKET:
            return [
                ...state,
                action.payload
            ]
        case REMOVE_QUADCOPTER_FROM_BASKET:
            return state.filter((id) => id !== action.payload)

        case CLEAN_BASKET: 
            return []
        default:
            return state
    }
}

