import { itemsByProp } from 'helpers/helpers';

import {
    FETCH_CATEGORIES_SUCCESS
} from 'actionTypes';

const initialState = {};

export default (state = initialState, action) => {
    switch(action.type) {
        case FETCH_CATEGORIES_SUCCESS: 
            const newValues = itemsByProp('id', action.payload);
            return {
                ...state, ...newValues
            }
            
        default: 
            return state
    }
}