import {
    FETCH_QUADCOPTER_BY_ID_START,
    FETCH_QUADCOPTER_BY_ID_SUCCESS
} from 'actionTypes';

const initialState = {
    id: null,
    fetching: false
}

export default (state = initialState, action) => {
    switch (action.type) {
        case FETCH_QUADCOPTER_BY_ID_START:
            return {
                ...state, fetching: true
            }
            
        case FETCH_QUADCOPTER_BY_ID_SUCCESS:
            return {
                ...state,
                id: action.payload.id, fetching: false
            }

        default:
            return state
    }
}