import {
    FETCH_QUADCOPTERS_SUCCESS,
    LOAD_MORE_QUADCOPTERS_SUCCESS,
    SEARCH_QUADCOPTER
} from 'actionTypes';

const initialState = {
    ids: [], 
    search: ""
};

export default (state = initialState, action) => {
    switch (action.type) {
        case FETCH_QUADCOPTERS_SUCCESS: 
        return {...state, 
                ids: action.payload.map( (quadcop) => quadcop.id )    
            }

        case LOAD_MORE_QUADCOPTERS_SUCCESS:
            const ids = action.payload.map( (quadcop) => quadcop.id);
            return {...state,
                ids: [...state.ids, ...ids]
            }
        
        case SEARCH_QUADCOPTER:
            return {
                ...state,
                search: action.payload
            }

        default: 
            return state
    }
}
