import {
    FETCH_QUADCOPTERS_START,
    FETCH_QUADCOPTERS_SUCCESS,
    LOAD_MORE_QUADCOPTERS_START,
    LOAD_MORE_QUADCOPTERS_SUCCESS,
    SEARCH_QUADCOPTER
} from 'actionTypes';

const initialState = {
    ids: [], 
    search: "",
    fetching: false
};

export default (state = initialState, action) => {
    switch (action.type) {
        case FETCH_QUADCOPTERS_START:
            return {
                ...state, fetching: true
            }

        case FETCH_QUADCOPTERS_SUCCESS: 
            return {...state, 
                    ids: action.payload.map( (quadcop) => quadcop.id ),
                    fetching: false    
                }

        case LOAD_MORE_QUADCOPTERS_START:
            return {
                ...state, fetching: true
            }

        case LOAD_MORE_QUADCOPTERS_SUCCESS:
            const ids = action.payload.map( (quadcop) => quadcop.id);
            return {...state,
                ids: [...state.ids, ...ids],
                fetching: false
            }
        
        case SEARCH_QUADCOPTER:
            return {
                ...state,
                search: action.payload
            }

        default: 
            return state
    }
}
