import * as R from 'ramda';
import { getObjById, objValuesToArr, addPropToObj } from 'helpers/helpers';

export const getQuadcopterById = (state, id) => getObjById(id, state.quadcopters); 

export const getQuadcopters = (state, ownProps) => {

    const activeCategoryId = getActiveCategoryId(ownProps);

    const applySearch = item => R.contains(
        state.quadcoptersPage.search.toLowerCase(),
        R.prop('name', item).toLowerCase()
    );
    const applyCategory = item => 
        activeCategoryId === getObjById('categoryId', item);

    const quadcopters = R.compose(
        R.filter(applySearch),
        R.when(R.always(activeCategoryId), R.filter(applyCategory)),
        R.map(id => getQuadcopterById(state, id))
    )(state.quadcoptersPage.ids)

    return quadcopters;
};

export const getRenderedQuadcoptersLength = state => state.quadcoptersPage.ids.length;

export const getTotalBasketCount = state => state.basket.length;

export const getTotalBasketPrice = state => {
    const totalPrice = R.compose(
        R.sum,
        R.pluck('price'),
        R.map(id => getQuadcopterById(state, id))
    )(state.basket)

    return totalPrice
};

export const getCategories = state => objValuesToArr(state.categories);

export const getActiveCategoryId = ownProps => ownProps.match.params.id;

export const getBasketQuadcoptersWithCount = state => {
    const uniqueIds = R.uniq(state.basket);

    const quadcopterCount = id => {
        return state.basket.filter(
            basketId => id === basketId
        ).length;
    }

    const quadcopterWithCount = quadcopter => {
        const newField = addPropToObj('count', quadcopterCount(quadcopter.id));
        return {
            ...quadcopter, ...newField
        }
    }

    const quadcopters = R.compose(
        R.map(quadcopterWithCount),
        R.map(id => getQuadcopterById(state, id))
    )(uniqueIds)

    return quadcopters
}